import './App.css';

const App = () => {

    let count = 1;

    const addHandler =() => {
        //alert('+1');
        count += 1;
    };


    const subHandler =() => {
        count -= 1;
        //alert('-1');
    };

    return <div className={'app'}>
        <h1>{count}</h1>
        <button onClick={subHandler}>-</button>
        <button onClick={addHandler}>+</button>
         </div>
};

export default App;
