import React from 'react';


const LogFilter = props =>{

    let display_y = props.year
    const changeHandler = e => {        
        props.onYearChange(+e.target.value);
        display_y = e.target.value
    };
    console.log('this is logfilter year value: '  + display_y)
    let y_val = props.year === undefined ? 2022 : display_y
    console.log('y_val value is ' + y_val)
    return(
        <div>
            Year: <select onChange={changeHandler}  value={display_y}>
                <option value="2023">2023</option>
                <option value="2022">2022</option>
                <option value="2021">2021</option>
                <option value="2020">2020</option>
            </select>
        </div>
    );
};

export default LogFilter
