import './App.css';

const App = () => {

    let count = 1;

    const addHandler =() => {
        //alert('+1');
        count += 1;
        document.getElementById('count_number').innerText = count;
        console.log('count value is ' + count);
    };


    const subHandler =() => {
        count -= 1;
        //alert('-1');
        document.getElementById('count_number').innerText = count;
        console.log('count value is ' + count);
    };

    return <div className={'app'}>
        <h1 id='count_number'>{count}</h1>
        <button onClick={subHandler}>-</button>
        <button onClick={addHandler}>+</button>
         </div>
};

export default App;
