import './App.css';
import {useState} from "react";

const App = () => {

    /**
     * 在React 中， 当组件渲染完毕，再修改组件中的变量，不会使组件重新渲染
     * 需要一个特殊的变量，当这个变量被修改时，组件就会重新渲染
     * 就是 State, State 对当前组件，其他组件无法访问
     * State 相当于一个变量，只是这个变量在react里进行了注册，react会监控这个变量
     * 
     * import {useState} from "react";
     * 需要一个值作为参数，这个值就是state的初始值
     * 该函数会返回一个数组
     * 数组中第一个元素，是初始值
     * 数组中第二个元素，是一个函数q
     * 
     */

    const result = useState('hello');
    console.log(result)

    //let count = 1;
    let count = result[0]
    const addHandler =() => {
        //alert('+1');
        count += 1;
        document.getElementById('count_number').innerText = count;
        console.log('count value is ' + count);
    };


    const subHandler =() => {
        count -= 1;
        //alert('-1');

        console.log('count value is ' + count);
    };

    return <div className={'app'}>
        <h1 id='count_number'>{count}</h1>
        <button onClick={subHandler}>-</button>
        <button onClick={addHandler}>+</button>
         </div>
};

export default App;
